import { vec3 } from 'gl-matrix';
import SpringBase from './SpringBase';
declare class SpringVec3 extends SpringBase {
    vel: vec3;
    val: vec3;
    tar: vec3;
    epsilon: number;
    setTarget(v: vec3): this;
    reset(v: vec3): this;
    update(dt: number): boolean;
}
export default SpringVec3;
